"use strict";let iconData={success:'fa-circle-check',error:'fa-circle-xmark',warning:'fa-circle-exclamation',info:'fa-circle-info'};let toastsID=0;let toasts=document.createElement("ul");toasts.className="toasts";document.body.appendChild(toasts);function createToast(id,text,autoclose=false,timer=5){if(toasts.childElementCount>=3)toasts.firstElementChild.className=="floatToast"?(node=>{document.getElementById(node.className).remove();node.remove()})(toasts.children[1]):(node=>{document.getElementById(node.className).remove();node.remove()})(toasts.firstElementChild);toastsID+=1;if(autoclose==false)timer=0;let thisToastsID="toast"+String(toastsID);let icon=iconData[id];let toast=document.createElement("li");toast.className="toast "+id+" "+thisToastsID;toast.innerHTML="<div class='column'><i class='fa-solid "+icon+"'></i><span>"+text+"</span></div>";let toastStyle=document.createElement("style");toastStyle.id=toast.className;toastStyle.innerText="."+thisToastsID+"::before {animation:progress "+timer+"s linear forwards;}";document.body.appendChild(toastStyle);toasts.appendChild(toast);if(autoclose==true)toast.timeoutId=setTimeout(()=>{let thisToast=toast.className;toast.classList.add("hide");if(toast.timeoutId)clearTimeout(toast.timeoutId);let floatToast=document.createElement("li");floatToast.className="floatToast";floatToast.style.height=String(toast.offsetHeight+10)+"px";setTimeout(()=>{try{toast.style.height="0px";toasts.insertBefore(floatToast,toast);toast.remove();document.getElementById(thisToast).remove()}catch(e){return}setTimeout(()=>{floatToast.style.height="0px";setTimeout(()=>{floatToast.remove()},400)},200)},400)},timer*1000)}
